"""
单针探底
. 昨天必须是阴线
. 今天最低价必须小于昨天最低价
. 今天的下影线大于实体长度两倍
. 今天的上影线小于实体长度
"""
def singleNeedleBottom(id, stock_info):
    if stock_info.shape[0] < 2:
        return False
    yesterday_info = stock_info.iloc[1]
    today_info = stock_info.iloc[0]

    if yesterday_info.p_change >= 0:
        return False
    if today_info.low > yesterday_info.low:
        return False

    if today_info.close >= today_info.open:
        return False

    if today_info.open >= today_info.close:
        upper_shadow_line = today_info.high - today_info.close
        lower_shadow_line = today_info.open - today_info.low
    else:
        upper_shadow_line = today_info.high - today_info.open
        lower_shadow_line = today_info.close - today_info.low
    entity = abs(today_info.open - today_info.close)

    if lower_shadow_line > entity*2 and entity >= upper_shadow_line:
        print('----------------- 单针探底, 股票代码: {0}'.format(id[num]))
        return True
    else:
        return False

def searchStock():
    fail_num = 0
    today = time.strftime("%Y-%m-%d", time.localtime())
    # cons = ts.get_apis()
    for num in range(0, len(id)):
        print('当前读取股票代码 {0}, 已经检索 {1}, 剩下 {2}'.format(id[num], num, len(id)-num))
        # info = ts.bar(id[num], conn=cons, start_date='2018-01-13', end_date='2018-03-14', ma=[5, 10, 20], adj='qfq')
        info = ts.get_hist_data(id[num], start='2019-03-21', end=today)

        if info is None:
            fail_num += 1
            continue
        else:
            singleNeedleBottom(id[num], info)

    print(fail_num)
    # ts.close_apis(cons)


if __name__ == '__main__':
    searchStock()

